void Pintar(int x, int color) {

	if (color == 0)Console::ForegroundColor = ConsoleColor::Black;
	if (color == 1)Console::ForegroundColor = ConsoleColor::DarkBlue;
	if (color == 2)Console::ForegroundColor = ConsoleColor::DarkMagenta;
	if (color == 3)Console::ForegroundColor = ConsoleColor::DarkRed;
	if (color == 4)Console::ForegroundColor = ConsoleColor::DarkYellow;
	if (x == 1) Console::BackgroundColor = ConsoleColor::Green;
	else if (x == 2)Console::BackgroundColor = ConsoleColor::Blue;
	else if (x == 3)Console::BackgroundColor = ConsoleColor::Black;
	else if (x == 4)Console::BackgroundColor = ConsoleColor::DarkBlue;
}



